    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            double value = Double.parseDouble(jTextField1.getText());
            switch (jComboBox1.getSelectedIndex()) {
                case 0 -> {
                    switch (jComboBox2.getSelectedIndex()) {
                        case 0 -> jTextField2.setText(String.valueOf(value));
                        case 1 -> jTextField2.setText(String.valueOf(String.format("%.2f", celsiusToFahrenheit(value))));
                        default -> jTextField2.setText(String.valueOf(String.format("%.2f", celsiusToKelvin(value))));
                    }
                }
                case 1 -> {
                    switch (jComboBox2.getSelectedIndex()) {
                        case 0 -> jTextField2.setText(String.valueOf(String.format("%.2f", fahrenheitToCelsius(value))));
                        case 1 -> jTextField2.setText(String.valueOf(value));
                        default -> jTextField2.setText(String.valueOf(String.format("%.2f", fahrenheitToKelvin(value))));
                    }
                }
                default -> {
                    switch (jComboBox2.getSelectedIndex()) {
                        case 0 -> jTextField2.setText(String.valueOf(String.format("%.2f", kelvinToCelsius(value))));
                        case 1 -> jTextField2.setText(String.valueOf(String.format("%.2f", kelvinToFahrenheit(value))));
                        default -> jTextField2.setText(String.valueOf(value));
                    }
                }
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Invalid value!!!");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

